import styled from 'styled-components'

import { Button } from 'antd'

import { CloseOutlined } from '@ant-design/icons'

const CARDBACKGROUND = '#f3f4f7'
const CARDBOXSHADOW = '#d5d6dc'
const CARDBOXSHADOWHOVER = '#aeaeae'
const LEVELTUBEBACKGROUND = '#e4e4e4'
const LEVELTUBEVALUEBACKGROUND = '#f3701a'
const DELETEBUTTONCOLOR = '#dc7777'

export const CloseIcon = styled(CloseOutlined)`
   :hover {
      font-size: 20px;
   }
`

export const DeleteButton = styled(Button)`
   display: none;
   position: absolute;
   top: 0;
   right: 0;
   border: none;
   background-color: transparent;
   /* font-size: 24px; */
   cursor: pointer;

   :hover {
      background-color: transparent;
      color: ${DELETEBUTTONCOLOR};
   }
`

export const Container = styled.div`
   width: calc(100% - 10px);
   background-color: ${CARDBACKGROUND};
   box-shadow: 1px 2px 5px ${CARDBOXSHADOW};
   position: relative;
   padding: 12px;

   :hover {
      box-shadow: 1px 2px 5px ${CARDBOXSHADOWHOVER};
   }

   &:hover ${DeleteButton} {
      display: block;
   }
`

export const CardImage = styled.img`
   max-width: 140px;
`

export const PokemonInformationContainer = styled.div`
   margin-left: 14px;
   position: absolute;
   top: 16px;
   display: inline-block;
   max-width: 200px;
   width: 100%;
`

export const PokemonName = styled.span`
   font-size: 25px;
   font-family: 'Gaegu', cursive;
`

export const PokemonInformation = styled.div`
   margin-bottom: 12px;
   display: grid;
   grid-template-columns: 22% auto;
   grid-row-gap: 8px;
   font-family: 'Atma', cursive;
`

export const StrengthGauge = styled.div`
   background-color: ${LEVELTUBEBACKGROUND};
   max-width: 180px;
   height: 20px;
   border-radius: 20px;
   width: 100%;
   position: relative;

   ::before {
      position: absolute;
      content: '';
      border-radius: 20px;
      height: 20px;
      background-color: ${LEVELTUBEVALUEBACKGROUND};
      max-width: 180px;
      top: 0px;
      left: 0px;
      width: ${(props) => (props.level ? `${props.level * 50 * 1.8}px` : 0)};
   }
`

export const HealthGauge = styled.div`
   background-color: ${LEVELTUBEBACKGROUND};
   max-width: 180px;
   width: 180px;
   height: 20px;
   border-radius: 20px;
   position: relative;

   ::before {
      position: absolute;
      content: '';
      border-radius: 20px;
      height: 20px;
      background-color: ${LEVELTUBEVALUEBACKGROUND};
      max-width: 180px;
      top: 0px;
      left: 0px;
      width: ${(props) =>
         props.health
            ? props.health < 100
               ? `${props.health * 1.8}px`
               : '400px'
            : 0};
   }
`

export const WeaknessGauge = styled.div`
   background-color: ${LEVELTUBEBACKGROUND};
   max-width: 180px;
   height: 20px;
   border-radius: 20px;
   position: relative;

   ::before {
      position: absolute;
      content: '';
      border-radius: 20px;
      height: 20px;
      background-color: ${LEVELTUBEVALUEBACKGROUND};
      max-width: 180px;
      top: 0px;
      left: 0px;
      width: ${(props) => (props.weak ? `${props.weak * 100 * 1.8}px` : 0)};
   }
`

export const StatusText = styled.span`
   font-size: 14px;
   line-height: 20px;
   margin-right: 8px;
   width: 40px;
`

export const StatusContainer = styled.div`
   display: flex;
   margin-bottom: 8px;
`

export const HappinessImage = styled.img`
   max-width: 25px;
`
